INSERT INTO Answers (AnswerID, QuestionID, AnswerLetter, AnswerText, AnswerWeight) VALUES (1698, 420, 'А', 'Само правата, произтичащи от Вашия радиолюбителски клас;', 0);
INSERT INTO Answers (AnswerID, QuestionID, AnswerLetter, AnswerText, AnswerWeight) VALUES (1699, 420, 'Б', 'Всички права, които позволява неговият радиолюбителски клас;', 1);
INSERT INTO Answers (AnswerID, QuestionID, AnswerLetter, AnswerText, AnswerWeight) VALUES (1700, 420, 'В', 'Клас на излъчване и максимална мощност, позволени за неговия радиолюбителски клас, но само в честотни обхвати, позволени за Вашия радиолюбителски клас;', 0);
INSERT INTO Answers (AnswerID, QuestionID, AnswerLetter, AnswerText, AnswerWeight) VALUES (1701, 420, 'Г', 'Честотни обхвати, позволени за неговия радиолюбителски клас, но само в клас на излъчване и максимална мощност, позволени за Вашия радиолюбителски клас.', 0);
INSERT INTO Answers (AnswerID, QuestionID, AnswerLetter, AnswerText, AnswerWeight) VALUES (1702, 421, 'А', 'След като получат от Вас писмено съгласие;', 0);
INSERT INTO Answers (AnswerID, QuestionID, AnswerLetter, AnswerText, AnswerWeight) VALUES (1703, 421, 'Б', 'След като придобият разрешително за радиолюбителска правоспособност и им се предостави за ползване опознавателен знак;', 1);
INSERT INTO Answers (AnswerID, QuestionID, AnswerLetter, AnswerText, AnswerWeight) VALUES (1704, 421, 'В', 'Само във Ваше присъствие;', 0);
INSERT INTO Answers (AnswerID, QuestionID, AnswerLetter, AnswerText, AnswerWeight) VALUES (1705, 421, 'Г', 'След като получат личен слушателски знак.', 0);
