INSERT INTO Answers (AnswerID, QuestionID, AnswerLetter, AnswerText, AnswerWeight) VALUES (1710, 423, 'А', 'При започване на радиовръзката и след това на всеки десет минути;', 0);
INSERT INTO Answers (AnswerID, QuestionID, AnswerLetter, AnswerText, AnswerWeight) VALUES (1711, 423, 'Б', 'При всяка реплика;', 0);
