INSERT INTO Answers (AnswerID, QuestionID, AnswerLetter, AnswerText, AnswerWeight) VALUES (1714, 424, 'А', 'Краткото си име, с което сте известен сред радиолюбителите;', 0);
INSERT INTO Answers (AnswerID, QuestionID, AnswerLetter, AnswerText, AnswerWeight) VALUES (1715, 424, 'Б', 'Фамилното си име и опознавателния знак;', 0);
