INSERT INTO Answers (AnswerID, QuestionID, AnswerLetter, AnswerText, AnswerWeight) VALUES (1719, 425, 'Б', 'Единият радиолюбител трябва да предаде опознавателния знак на кореспондента, другият да предаде своя опознавателен знак;', 0);
INSERT INTO Answers (AnswerID, QuestionID, AnswerLetter, AnswerText, AnswerWeight) VALUES (1720, 425, 'В', 'Всеки от радиолюбителите трябва да предаде опознавателния знак на кореспондента и своя опознавателен знак;', 1);
INSERT INTO Answers (AnswerID, QuestionID, AnswerLetter, AnswerText, AnswerWeight) VALUES (1721, 425, 'Г', 'Идентификация е необходима само при радиовръзка с чуждестранен радиолюбител.', 0);
