INSERT INTO Answers (AnswerID, QuestionID, AnswerLetter, AnswerText, AnswerWeight) VALUES (1729, 427, 'Г', 'Ако ползва мощност до 50 mW и програмата се чува само в радиус до 500 m.', 0);
INSERT INTO Answers (AnswerID, QuestionID, AnswerLetter, AnswerText, AnswerWeight) VALUES (1730, 428, 'А', 'Няма забрана да се излъчва музика от любителската радиостанция;', 0);
INSERT INTO Answers (AnswerID, QuestionID, AnswerLetter, AnswerText, AnswerWeight) VALUES (1731, 428, 'Б', 'Само ако се налага да се заглуши нелегитимно излъчваща радиостанция;', 0);
INSERT INTO Answers (AnswerID, QuestionID, AnswerLetter, AnswerText, AnswerWeight) VALUES (1732, 428, 'В', 'Никога;', 1);
INSERT INTO Answers (AnswerID, QuestionID, AnswerLetter, AnswerText, AnswerWeight) VALUES (1733, 428, 'Г', 'Само ако ползва мощност до 50 mW и радиовръзката не е през любителски ретранслатор.', 0);
INSERT INTO Answers (AnswerID, QuestionID, AnswerLetter, AnswerText, AnswerWeight) VALUES (1734, 429, 'А', 'Само по време на радиолюбителско състезание;', 0);
INSERT INTO Answers (AnswerID, QuestionID, AnswerLetter, AnswerText, AnswerWeight) VALUES (1735, 429, 'Б', 'Никога;', 1);
INSERT INTO Answers (AnswerID, QuestionID, AnswerLetter, AnswerText, AnswerWeight) VALUES (1736, 429, 'В', 'Само при радиовръзка с нелюбителска радиостанция;', 0);
