INSERT INTO Answers (AnswerID, QuestionID, AnswerLetter, AnswerText, AnswerWeight) VALUES (1740, 430, 'В', 'Само ако станцията започне да предава на използваната от Вас честота;', 0);
INSERT INTO Answers (AnswerID, QuestionID, AnswerLetter, AnswerText, AnswerWeight) VALUES (1741, 430, 'Г', 'Само когато и другата станция умишлено смущава Вашето излъчване.', 0);
